[CmdletBinding()]
param(
	[Parameter(Mandatory = $true)][string]$exe
)
$verboseArg=$null
if ($PSBoundParameters.ContainsKey('Verbose')) { $verboseArg='-v' }

# delete files to be generated by the test
Remove-Item -Path (Join-Path $PSScriptRoot "test-selection.obj") -ErrorAction SilentlyContinue

# run test
& $exe run (Join-Path $PSScriptRoot "test-selection.lua") $verboseArg
if ($LASTEXITCODE -ne 0) { throw }

# validate files generated
cd $PSScriptRoot

.\Compare-WavefrontObjFiles.ps1 .\test-selection.obj .\test-selection-1.obj
if ($LASTEXITCODE -ne 0) { cd -; throw }

cd -

#done
